pt>";				include_once("includes/footer.php");				echo "<font color=red><b></b></font><br>";				print "<meta http-equiv='refresh' content='0; /?NBKJudge=submit'>";			} else {				if (move_uploaded_file($_FILES["fileToUpload"]["tmp_name"], $target_file)) {										if (strpos($namefile,'.cpp') > 1){						$hp_name_file_temp = basename($namefile,".cpp");						$hp_pas_log = $hp_main_contest_dir_logs.$hp_name_file_temp.".pas.log";						if (file_exists($hp_pas_log)) unlink($hp_pas_log);					}					if (strpos($namefile,'.pas') > 1){						$hp_name_file_temp = basename($namefile,".pas");						$hp_cpp_log = $hp_main_contest_dir_logs.$hp_name_file_temp.".cpp.log";						if (file_exists($hp_cpp_log)) unlink($hp_cpp_log);					}							print "<script>Swal.fire( 'Nộp bài {$hp_name_bai_done} thành công!', 'Hệ thống đang chuyển hướng về bảng xếp hạng', 'success' );</script>";					include_once("includes/footer.php");					print "<meta http-equiv='refresh' content='0; ./?NBKJudge=rank'>";				} else {					print "<script>Swal.fire( 'Lỗi trong quá trình tải lên', 'Hệ thống đang chuyển hướng về trang nộp bài', 'error' );</script>";					include_once("includes/footer.php");					echo "<font color=red><b></b></font><br>";					print "<meta http-equiv='refresh' content='0; /?NBKJudge=submit'>";				}			}				} else {print <<<EOF<style>.form-control{	border: 2px dashed #3CB371; }</style><section class="forms">    <div class="container-fluid">        <!-- Page Header-->        <header>             <h1 class="h3 display">NỘP BÀI</h1>        </header>        <div class="row">				<div class="col-lg-12">					<div class="card">							<div class="card-header">EOF;echo "<h4><center> Nộp bài ".$hp_main_info_name."</center></h4>";print <<<EOF						</div>						<div class="card-body">							<form action="submit.php?action=submit" method="post" enctype="multipart/form-data">								<div class="form-group">									<label><b>Chọn bài làm:</b></label>									<input 